// Basic Routing
 import React from "react";

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom"

export default function App() {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/users">Users</Link>
            </li>
          </ul>
        </nav>

        <Switch>

          <Route path="/about">
            <About />
          </Route>

          <Route path="/users">
            <Users />
          </Route>

          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  )
}

function Home(){
  return( 
  <h2>Home </h2>,
  <p>This is fucking home session .</p>
  )
}
function About(){
  return(
    <h2>About</h2>,
    <p>This is fucking about session .</p>
  )
}
function Users()
{
  return(
    <h2>Users</h2>,
    <p>This is fucking users session</p>
  )
} 

